
public class NaveenPositive_NegetiveNumber {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		double number = 100;

		if (number > 0) {
			System.out.println(number + " The given number is Positive");
		} else if (number < 0) {
			System.out.println(number + " The given number is Negetive");
		} else {
			System.out.println(number + " The given number is Zero");
		}

	}

}
